import React from 'react'
import { motion, useTransform, useViewportScroll } from 'framer-motion'

import { Container } from './styles';
import { Sticky } from '../../styles';



  const FirstAndSecond: React.FC = () => {
    const { scrollYProgress } = useViewportScroll()

    const frameOpacity = useTransform(scrollYProgress, [0.196, 0.198], [0, 1])
    const frameScale = useTransform(scrollYProgress, [0.558, 0.627], [0.511, 0.8])

  return (
    <Sticky className="second">
      <First/>


      <motion.div style={{
        position: 'absolute',
        top: 0,
        left: 0,
        right: 0,
        height: '100vh',
        borderRadius: '4px',
        border: '4px solid #fff',
        opacity: frameOpacity,
        scale: frameScale
      }}
    />
    </Sticky>
  );
};

const First: React.FC = () => {
  const { scrollYProgress } = useViewportScroll()

  const firstScale = useTransform(scrollYProgress,
    [0.198, 0.264, 0.558, 0.627],
    [1, 0.511, 0.511, 1]
    )
    const firstRadius = useTransform(scrollYProgress,
      [0.198, 0.264, 0.558, 0.627],
      [0, 4, 4, 0]
    )
    const leftSideHeight = useTransform(scrollYProgress,
      [0, 0.058],
      ['20vh', '100vh']
    )
    const rightSideScale = useTransform(
      scrollYProgress,
      [0.047, 0.093],
      [0, 0.511]
    )
    const rightSideY = useTransform(
      scrollYProgress,
      [0.047, 0.093],
      ['58vh', '0vh']
    )
    const offsetY = useTransform(
      scrollYProgress,
      [0.328, 0.397],
      ['0%', '-100%']
    )


  return<Sticky className="first"
  style={{
    scale: firstScale,
    borderRadius: firstRadius 
  }}
  >
    <motion.div className="offset" style={{
      y: offsetY
    }}>
      <div className="a">
        <motion.div
          className="left-side"
          style={{
            height: leftSideHeight
          }}
        />
        <div className="right-side">
          <motion.div
            className="right-image"
            style={{
              y: rightSideY,
              scale: rightSideScale,
            }}
          />
        </div>
      </div>
      <div className="b"></div>
      <div className="c"></div>
    </motion.div>
  </Sticky>
}
export default FirstAndSecond;
